import React, { Component } from 'react';
  import axios from 'axios';

  class Posts extends Component {
    constructor() {
      super();
      this.getPrivatePosts = this.getPrivatePosts.bind(this);
      this.state = {
        postData: []
      };
    }
    getPrivatePosts() {
      axios.get(`http://localhost:3000/Posts/private`).then(res => {
        const postData = res.data;
        this.setState({ postData });

        var g = new Date(postData.map( timestamp => ( timestamp.LastEdited )));
        // Split timestamp into [ Y, M, D, h, m, s ]
        var t = g.split(/[- :]/);

        // Apply each element to the Date function
        var d = new Date(Date.UTC(t[0], t[1]-1, t[2], t[3], t[4], t[5]));

       console.log(d);
        // -> Wed Jun 09 2010 14:12:01 GMT+0100 (BST)
      });
    }

    render() {
      return (
        <div>
          <button className="button" onClick={this.getPrivatePosts}>
            Show Private Posts
          </button>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>UserID</th>
                <th>Title</th>
                <th>Content</th>
                <th>isPrivate</th>
                <th>LastEdited</th>
              </tr>
            </thead>
            <tbody>
              {this.state.postData.map(post => (
                <tr key={post.ID}>
                  <td>{post.ID}</td>
                  <td>{post.UserID}</td>
                  <td> {post.Title}</td>
                  <td>{post.Content}</td>
                  <td>{post.isPrivate}</td>
                  <td>{post.LastEdited}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  }

  export default Posts;
