import React, { Component } from 'react';
import { FormGroup , Label, Input, Button } from 'reactstrap';
import axios from 'axios';
class CommentField extends Component {
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.updateState = this.updateState.bind(this);
        this.state = {
            ID: '',
            UserID: '',
            PostID: '',
            Message: ''
        }; 
    }
    

    updateState = e => {
        const currUserId = sessionStorage.getItem("userID");
        const currPostId = sessionStorage.getItem("PostID")
        const currMessage = e.target.value;

        this.setState({ UserID: currUserId, PostID: currPostId, Message: currMessage });
        console.log(this.state);
    }

    handleSubmit = e => {
        e.preventDefault();

        const { PostID, ID, UserID, Message  } = this.state;
        if (Message != null){

            // axios
            //     .post('http://localhost:3000/comments', { ID, PostID, UserID, Message })
            //     .then(res => {
                //     console.log(res);
                //     console.log(res.data);
                //     });
            } else {
                alert("Cant post empty comment!");
            }
    };
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                <FormGroup>
                    <Label for="Message">Text Area</Label>
                    <Input type="textarea" onKeyUp={this.updateState} name="Message" id="Message" />
                    <Button type="submit">Comment</Button>
                </FormGroup>
                </form>
            </div>
        );
    }
}

export default CommentField;
