import React, { Component } from 'react';

import axios from 'axios';

class PostsByCategory extends Component {
constructor() {
    super();
    this.getPosts = this.getPosts.bind(this);
    this.state = {
      posts: []

    };
  }
  getPosts() {
    const category = document.getElementsByClassName("dropdown-item").value;
    axios.get(`http://localhost:3000/category/` + category).then(res => {
      const posts = res.data;
      this.setState({ posts });
    });
  }
    render() {
        return (
        <div>
          <div className="dropdown" onClick={this.toggleOpen}  >
                <button 
                    className="btn btn-secondary dropdown-toggle" 
                    type="button" 
                    id="dropdownMenuButton" 
                    data-toggle="dropdown" 
                    aria-haspopup="true"
                                      >
                    Categories
                </button>
                <div className={menuClass} aria-labelledby="dropdownMenuButton">
                    <a className="dropdown-item" href="http://localhost:3001/Posts/lifestyle">Lifestyle</a>
                    <a className="dropdown-item" href="http://localhost:3001/Posts/gaming">Gaming</a>
                    <a className="dropdown-item" href="http://localhost:3001/Posts/technology">Technology</a>
                    <a className="dropdown-item" href="http://localhost:3001/Posts/vehicles">Vehicles</a>
                </div>

            </div>
        </div>
        );
    }
}

export default PostsByCategory;
