import React, { Component } from 'react';
  import axios from 'axios';

  class Posts extends Component {
    constructor() {
      super();
      this.getPrivatePosts = this.getPrivatePosts.bind(this);
      this.state = {
        allPrivatePosts: []
      };
    }
    getPrivatePosts() {
      axios.get(`http://localhost:3000/Posts/private`).then(res => {
        const privatePosts = res.data;
        this.setState({ privatePosts });
      });
    }

    render() {
      return (
        <div>
          <button className="button" onClick={this.getPrivatePosts}>
            Show Private Posts
          </button>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>id</th>
                <th>user_id</th>
                <th>title</th>
                <th>content</th>
                <th>isPrivate</th>
                <th>LastEdit</th>
              </tr>
            </thead>
            <tbody>
              {this.state.privatePosts.map(allPrivatePosts => (
                <tr key={allPrivatePosts.ID}>
                  <td>{allPrivatePosts.UserID}</td>
                  <td> {allPrivatePosts.Title}</td>
                  <td>{allPrivatePosts.Content}</td>
                  <td>{allPrivatePosts.isPrivate}</td>
                  <td>{allPrivatePosts.LastEdit}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  }

  export default Posts;
