import React, { Component } from 'react';
import axios from 'axios';
import Moment from 'react-moment';


class CommentsList extends Component {
    constructor(props){
        super(props);
        this.state = {
            comments: [],
            ID: '',
            UserID: '',
            PostID: '',
            Message: '',
            LastEdit: '',
            username: '',
        }; 
    }

    componentDidMount(){
        const currPostId = sessionStorage.getItem("PostID")

        axios.get(`http://localhost:3000/comments/`+currPostId).then(results => {
          const comments = results.data;
          const ID = [];
          const UserID = [];
          const PostID = [];
          const Message = [];
          const LastEdit = [];
          for (var x = 0; x < comments.length; x++) {
            ID.push(comments[x].ID);
            UserID.push(comments[x].UserID);
            PostID.push(comments[x].PostID);
            Message.push(comments[x].Message);
            LastEdit.push(comments[x].LastEdit);
          }
          this.setState({ 
            comments,
            ID,
            UserID,
            PostID,
            Message,
            LastEdit
           });
        });
        console.log(this.state);

    }

    
    shouldComponentUpdate() {
        if(this.state.PostID != sessionStorage.getItem('PostID')){
            return true;
        } else {
            return false;
        }
    }
    

    

    render() {
        
        let commentsList = this.state.comments.map ( comment => {
        const commentUser = comment.UserID;
        const Messagestring = comment.Message;
            
        
        return (
            <div className="for-posts">
            <div className="commentdiv">
                <ul id={comment.ID} >
                <li>ID: <i>{comment.ID}</i></li>
                <li><h3>{commentUser}</h3></li>
                <li><p>{Messagestring}</p></li>
                <br/>
                <Moment format={"DD-MM-YYYY"}>
                    <li>{comment.LastEdit}</li>
                </Moment>
                </ul>
            </div>
            </div>)
            });
        return (
            <div>
                {commentsList}
            </div>
        );
    }
}
        
export default CommentsList;
