import React, { Component } from 'react';
import axios from 'axios';

class ShareEntry extends Component {


    handleSubmit = event => {
        event.preventDefault();

        const { PostID, Owner, Viewer } = this.state;
        window.location.reload();

        axios
            .post('http://localhost:3000/Shared/', { PostID, Owner, Viewer })
            .then(res => {
            console.log(res);
            console.log(res.data);
            });
        };

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input name="ID" placeholder="ID"></input>
                    <lable>FOR TEST PURPOSES POST ID:::::::</lable>
                    <input type="number" name="PostID" placeholder="PostID"></input>
                    
                    <lable>Share my with:</lable> 
                    <input value="arne" name="Owner" disabled></input>

                    <input onkeyDown="getUsers()" type="text" name="Viewer" placeholder="type in username"></input>
                    <button type="submit">Share</button>
                </form>
            </div>
        );
    }
}

export default ShareEntry;
