import React, { Component } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import OptionModal from './Components/OptionModal';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Badge } from 'reactstrap';
import { Route, Link } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Wholepost from './Components/Wholepost';
import NavigationButton from './Components/NavigationButton';
import CommentField from './Components/CommentField';

import RemoveButton from './Components/RemoveButton';

class Test extends Component {

  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      ID: '',
      Title: '',
      Content: '',
      LastEdit: '',
      modal: false
    };
    this.handleRemove = this.handleRemove.bind(this);
    this.handleModal = this.handleModal.bind(this);
  }

  componentDidMount() {
    axios.get(`http://localhost:3000/posts/public`).then(results => {
      const posts = results.data;
      const ID = results.data;
      const Title = results.data;
      const Content = results.data;
      const LastEdit = results.data;
      this.setState({ 
        posts,
        ID,
        Title,
        Content,
        LastEdit,
       });
    });
  }

    handleRemove = ID => {
    this.setState ({
      posts: this.state.posts.filter( Post => Post.ID !== ID)
    });
  };

  handleModal(ID) {
    this.setState({
        modal: !this.state.modal,
        ID: ID

    });
}


render() {
    let postsList = this.state.posts.map (Post => {
        return (
        <div className="for-posts">
        <div className="postdiv" key={Post.ID}>
          <ul>
            <li>{Post.Title}</li>
            <li>{Post.Content}</li>
          </ul>
        </div>
        </div>)
      })
    return(
      <div>
        	<div>
            {postsList}
        </div>
        <div>
          {this.state.posts.map((Post) => 
          <Modal isOpen={this.state.modal} toggle={this.handleModal} className={this.props.className}>
            <ModalHeader>{Post.ID}</ModalHeader>
            <ModalBody >
              
            </ModalBody>
            <CommentField/>
          </Modal>
          )}
        </div>
      </div>
    );
}
}

export default Test;
