import React, { Component } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import OptionModal from './Components/OptionModal';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Badge } from 'reactstrap';
import { Route, Link } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Wholepost from './Components/Wholepost';
import NavigationButton from './Components/NavigationButton';

import RemoveButton from './Components/RemoveButton';

class Test extends Component {

  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      ID: '',
      modal: false
    };
    this.handleRemove = this.handleRemove.bind(this);
    this.handleModal = this.handleModal.bind(this);
  }

  componentDidMount() {
    axios.get(`http://localhost:3000/posts/public`).then(results => {
      const posts = results.data;
      this.setState({ posts });
    });
  }

    handleRemove = ID => {
    this.setState ({
      posts: this.state.posts.filter( Post => Post.ID !== ID)
    });
  };

  handleModal() {
    this.setState({
        modal: !this.state.modal
    });
}


render() {
  const posts = this.state.posts;
    return(
      <div>
        <div className="for-posts">
          <ul>{posts}</ul>
        </div>
        <div>
          {this.state.posts.map((Post) =>(
          <Modal isOpen={this.state.modal} toggle={this.handleModal} className={this.props.className}>
            <ModalHeader key={Post}>{Post.Title}</ModalHeader>
            <ModalBody key={Post}>
              {Post.Content}
            </ModalBody>
          </Modal>
          ))}
        </div>
      </div>
    );
}
}

export default Test;
