import React, { Component } from 'react';
import axios from 'axios';

class Borrowers extends Component {
    constructor() {
        super();
        this.getBooks = this.getBooks.bind(this);
        this.state = {
            borrowers: []
        };
    }
    getBooks() {
        axios.get(`http://localhost:3000/borrowers`).then(res => {
            const borrowers = res.data;
            this.setState({ borrowers });
        });
    }

    render() {
        return (
            <div>
                <button className="button" onClick={this.getBooks}>
                Show borrowers
                </button>
                <table className="table table-bordered">
                    <thead>
                    <tr>
                        <th>borrower_id</th>
                        <th>firstname</th>
                        <th>lastname</th>
                        <th>phone</th>
                        <th>streetAddress</th>
                        <th>postalCode</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.borrowers.map(book => (
                        <tr key={book.book_id}>
                        <td>{book.book_id}</td>
                        <td> {book.book_name}</td>
                        <td>{book.author}</td>
                        <td>{book.isbn}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default Borrowers;
