import React, { Component } from 'react';

class PostView extends Component {
    constructor(){
        super();
        this.getPosts = this.getPosts.bind(this);
    }

    getPosts() {
        axios.get(`http://localhost:3000/posts/:ID`).then(res => {
          const posts = res.data;
          this.setState({ posts });
        });
      }
    render() { 
        return (
            <div>
                <h1></h1>
            </div>
        );
    }
}

export default PostView;
