import React, { Component } from 'react';
import axios from 'axios';

class CategoryTest extends Component {
    constructor() {
        super();
        this.onChange = this.onChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            ID: '',
            PostID: ''
        }; 
    }
    onChange = e => {
        const state = this.state;
        state[e.target.name] = e.target.value;
        this.setState(state);
    };
    handleSubmit = event => {
        event.preventDefault();

        const { ID, PostID } = this.state;
       

        axios
            .post('http://localhost:3000/technology', { ID, PostID  })
            .then(res => {
            console.log(res);
            console.log(res.data);
            });
        };

    render() {
        return (
            <div>
                <h4>Give category to a post</h4>
                <form onSubmit={this.handleSubmit}>
                <label>
                    PostID:
                    <input type="number" name="PostID" />
                </label>
                <br/>
                <button className="button" name="submit">Submit</button>
                </form>
                
            </div>
        );
    }
}

export default CategoryTest;
