import React, { Component } from 'react';
import { Badge } from 'reactstrap';
import axios from 'axios';

  class PostsByCategory extends Component {
    constructor() {
      super();
      this.getCategory = this.getCategory.bind(this);
      this.state = {
        Categories: []
      };
    }

  getCategory() {
    const item1 = "lifestyle"
    const item2 = "gaming"
    const item3 = "vehicles"
    const item4 = "technology"
    const url = window.location.href
    if (url.includes(item1)) {
      axios.get(`http://localhost:3000/Posts/category/` + item1).then(res => {
        const Categories = res.data;
        this.setState({ Categories });
      });}
    if (url.includes(item2)) {
      axios.get(`http://localhost:3000/Posts/category/` + item2).then(res => {
        const Categories = res.data;
        this.setState({ Categories });
      });}
    if (url.includes(item3)) {
      axios.get(`http://localhost:3000/Posts/category/` + item3).then(res => {
        const Categories = res.data;
        this.setState({ Categories });
      });}
    if (url.includes(item4)) {
      axios.get(`http://localhost:3000/Posts/category/` + item4).then(res => {
        const Categories = res.data;
        this.setState({ Categories });
      });}
    
    }
    handleModal (search) {
      console.log(search)
      var idlist = []
      for (var x = 0; x < this.state.posts.length; x++) {
  
        idlist.push(this.state.posts[x].ID);
  
        this.setState({
          modal: !this.state.modal,
        });  
      }
  
      this.forlooptitle(idlist, search);
  }
  
  handleClose() {
    this.setState({
      modal: !this.state.modal,
  });
  }

    render() {
      let postsList = this.state.posts.map ( Post => {
        return (
        <div className="for-posts">
        <div className="postdiv">
          <ul onClick={() => this.handleModal(Post.ID)} id={Post.ID} >
            <li>ID: <i>{Post.ID}</i></li>
            <li><h3>{Post.Title}</h3></li>
            <li><p>{Post.Content}</p></li>
            <Badge>{Post.Category}</Badge>
            <br/>
            <Moment format={"DD-MM-YYYY"}>
              <li>{Post.LastEdit}</li>
            </Moment>
          </ul>
        </div>
        </div>)
      });
          var modaltitle =
            <div id="title">
              
              {this.state.titletext}
            
            </div>
            ;
          var modalcontent =
            <div>
              
              {this.state.titlecontent}
            
            </div>
            ;
    
        return(
          <div>
            <div>
              {postsList}
            </div>
            
            <Modal key={this.props.ID} isOpen={this.state.modal} key={this.state.posts.ID} className="modalpost">
              
                <ModalHeader>{modaltitle}</ModalHeader>
                <ModalBody>
                  {modalcontent}
                </ModalBody>
                
                  <CommentField/>
                <ModalFooter>
                  <Button color="secondary" onClick={this.handleClose}>Close</Button>
                </ModalFooter>
            </Modal>
          </div>
        );
    }
  }

  export default PostsByCategory;
