import React, { Component } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import OptionModal from './Components/OptionModal';
import { Badge } from 'reactstrap';
import { Route, Link } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";


class Test extends Component {

  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
      posts: [],
      text: '',
      ID: ''
    };
    this.removePost = this.removePost.bind(this);
  }

  componentDidMount() {
    axios.get(`http://localhost:3000/posts/public`).then(results => {
      const posts = results.data;
      this.setState({ posts });
    });
  }

  handleSubmit = event => {
    event.preventDefault();
    const id = this.state.ID;
    const { ID } = this.state;

    axios.delete('http://localhost:3000/posts/' + id, {ID})
    .then(res => {
      console.log(res);
      console.log(res.data);
      });
  };

  removePost(name, i){
    let posts = this.state.posts.slice();
    posts.splice(i, 1);
    this.setState({ posts });
  }


render() {
    return(
      <div>
        <div className="for-posts">
            {this.state.posts.map(Post =>(
              <div className="postdiv" data-id={Post.ID}>
              <p className="for-id">#{Post.ID} Last edited: {Post.LastEdit}</p>
              <h4>{Post.Title} <Badge color="info" className="category-badge">{Post.Category}</Badge></h4>
              <p>{Post.Content}</p>
                return <button onClick ={() => {this.removePost(Post, i)}} key={i}></button>
              </div>
            ))}
        </div>
      </div>
    );
}
}

export default Test;
