import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import './index.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Navbar, NavItem, NavDropdown, MenuItem } from 'react-bootstrap';
import BookTable from './BookTable';
import AddBook from './AddBook';
import UpdateBook from './UpdateBook';
import DeleteBook from './DeleteBook';
import BorrowerTable from './BorrowerTable';
import AddBorrower from './AddBorrower';
import UpdateBorrower from './UpdateBorrower';
import DeleteBorrower from './DeleteBorrower';
import { NavLink, Route } from 'react-router-dom';
require("react-bootstrap/lib/NavbarHeader");
require("react-bootstrap/lib/NavbarBrand")



class App extends Component {
  render() {
    return (
      <div className="App">
        <navbar className="App-navbar">
          <Navbar.Header>
            <Navbar.Brand>
              <h1>Menu</h1>
            </Navbar.Brand>
          </Navbar.Header>
          <nav className="App-nav">
            <NavItem eventkey={1} href="/">Home</NavItem>
            <NavDropdown eventKey={2} title="Books" id="books-dropdown">
              <MenuItem eventkey={2.1}>Add Book</MenuItem>
              <MenuItem eventkey={2.2}>Update Book</MenuItem>
              <MenuItem eventkey={2.3}>Delete Book</MenuItem>
            </NavDropdown>
            <NavDropdown eventKey={3} title="Borrowers" id="borrowers-dropdown">
              <MenuItem eventkey={3.1}>Add Borrower</MenuItem>
              <MenuItem eventkey={3.2}>Update Borrower</MenuItem>
              <MenuItem eventkey={3.3}>Delete Borrower</MenuItem>
            </NavDropdown>
          </nav>
        </navbar>
        <br/>
        <Route path="/" exact />

        <Route path="/books" exact component={AddBook} /> 
        <Route path="/books" exact component={UpdateBook} />
        <Route path="/books" exact component={DeleteBook} /> 
        <Route path="/books" exact component={BookTable} /> 

        <Route path="/borrowers" exact component={AddBorrower} /> 
        <Route path="/borrowers" exact component={UpdateBorrower} /> 
        <Route path="/borrowers" exact component={DeleteBorrower} />
        <Route path="/borrowers" exact component={BorrowerTable} /> 
      </div>

    );
  }
}

export default App;
