import React, { Component } from 'react';
import axios from 'axios';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import "react-toastify/dist/ReactToastify.css";
import CommentField from './Components/CommentField';
import 'moment-timezone';
import Moment from 'react-moment';
import { Link, Route } from 'react-router-dom';
import swal from 'sweetalert2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faShareAlt, faEdit } from '@fortawesome/free-solid-svg-icons';
import UserMenu from "./Components/UserMenu";
import EditPost from "./EditPost";

class Test extends Component {

  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      Users: [],
      alreadyShared: [],
      ID: '',
      Title: '',
      Content: '',
      LastEdit: '',
      titletext: '',
      titlecontent: '',
      PostID: '',
      Owner: '',
      currOwner: '',
      Viewer: '',
      currViewer: '',
      modal: false
    };

        this.handleModal = this.handleModal.bind(this);
        this.forlooptitle = this.forlooptitle.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.shareEntry = this.shareEntry.bind(this);
        this.Owner = this.Owner.bind(this);
        this.handleShare = this.handleShare.bind(this);
        this.sharedViewer = this.sharedViewer.bind(this);
  }

  componentDidMount() {

    this.getUsers();
    this.Owner();



    axios.get(`http://localhost:3000/posts/public`).then(results => {
      const posts = results.data;
      const ID = [];
      const Title = [];
      const Content = [];
      for (var x = 0; x < posts.length; x++) {
        ID.push(posts[x].ID);
        Title.push(posts[x].Title);
        Content.push(posts[x].Content);
      }
      this.setState({ 
        posts,
        ID,
        Title,
        Content,
       });
    });
  }

  deletePost = (ID) => {
    axios.delete(`http://localhost:3000/posts/${ID}`)
        .then(data => {
          if(data.status) {
            const post = [...this.state.posts];
            let results = post.filter( post => (
                post.ID !== ID
            ));
            this.setState({
              posts: results
            });
          }
        })
  };

  confirmDeletion (ID) {
    swal({
      title: 'Are you sure?',
      text: "This will permanently delete the post",
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Continue',
      cancelButtonText: 'Cancel'
    }).then((result) => {
      if (result.value) {
        this.deletePost(ID);
        swal(
            'Deleted!',
            'Post has been deleted!',
            'success'
        )
      }
    })
  };



  getUsers() {
      axios.get(`http://localhost:3000/Users/`).then(res => {
        const Users = res.data;
        this.setState({ Users });
      });
      
    } 

  Owner() {
      const state = this.state;
      state["currOwner"] = JSON.parse(sessionStorage.getItem("userUsername"));
      this.setState(state);
  }

  shareEntry (currPostID) {
    this.setState({PostID: currPostID});
    swal({
      title: 'Type in Persons Username',
      input: 'text',
      showCancelButton: true,
        cancelButtonColor: '#d33',
        inputValidator: (value) => {
        return !value && 'You need to write something!'
      }
    }).then((inputValue) => {
     
      if (inputValue) {
        
        this.setState({currViewer: inputValue.value,
                      PostID: currPostID})
        this.sharedViewer();
      }
    });
  }
  

  sharedViewer(){
    const currOwner = this.state.currOwner;
    axios.get('http://localhost:3000/Shared/mine/'+ currOwner).then(res => {
      const alreadyShared = res.data;
      const selectedPost = [];

        for( var x=0; x < alreadyShared.length; x++) {
        
          if (alreadyShared[x].PostID == this.state.PostID) {
            selectedPost.push(alreadyShared[x].Viewer)
            this.setState({alreadyShared: selectedPost});
          }
        }
    })
    this.handleShare();
  }

  handleShare () {

    var currViewer = this.state.currViewer;
    
    const { PostID, currOwner, Viewer } = this.state;
    var CheckUsername = this.state.Users.map(user => (user.username));

    for( var x = 0; x < this.state.alreadyShared.length; x++){
      
      if( currViewer != this.state.currOwner && CheckUsername[x] == currViewer && this.state.alreadyShared[x] != currViewer ) {

            console.log('Posted')
            console.log(this.state)
            break
      //   axios
