      //       'success')
      //     } else {
      //       swal ('Error,',
      //       'Entry was not shared.',
      //       'error')
      //     }
      //   });
      }else { console.log('why')}     
    }  

    // for( x = 0; x < this.state.alreadyShared[x].length; x++) {
    //   if( CheckUsername[x] == this.state.currOwner && CheckUsername[x] == currViewer ){

    //     swal(
    //       'Woops!',
