import React, { Component } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import OptionModal from './Components/OptionModal';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Badge } from 'reactstrap';
import { Route, Link } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Wholepost from './Components/Wholepost';
import NavigationButton from './Components/NavigationButton';

import RemoveButton from './Components/RemoveButton';

class Test extends Component {

  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      ID: '',
      modal: false
    };
    this.handleRemove = this.handleRemove.bind(this);
    this.handleModal = this.handleModal.bind(this);
  }

  componentDidMount() {
    axios.get(`http://localhost:3000/posts/public`).then(results => {
      const posts = results.data;
      this.setState({ posts });
    });
  }

    handleRemove = ID => {
    this.setState ({
      posts: this.state.posts.filter( Post => Post.ID !== ID)
    });
  };

  handleModal() {
    this.setState({
        modal: !this.state.modal
    });
}


render() {

    return(
      <div>
        <div className="for-posts">
            {this.state.posts.map(Post =>(
            <ul>
              <div className="postdiv" data-id={Post.ID}>
              <RemoveButton {...Post} removePost={this.handleRemove} />
              <p className="for-id">#{Post.ID} Last edited: {Post.LastEdit}</p>
                <h4>{Post.Title}</h4><Badge color="info" className="category-badge">{Post.Category}</Badge>
              <p className="text">{Post.Content}</p>
              <Button onClick={this.handleModal}>Open</Button>
              </div>
            </ul>
            
            ))}
        </div>
        <div>
          {this.state.posts.map(Post =>(
          <Modal isOpen={this.state.modal1} toggle={this.handleModal} className={this.props.className}>
            <ModalHeader>{Post.Title}</ModalHeader>
            <ModalBody>
              {Post.Content}
            </ModalBody>
          </Modal>
          ))}
        </div>
      </div>
    );
}
}

export default Test;
