import React, { Component } from 'react';
  import axios from 'axios';

  class Posts extends Component {
    constructor() {
      super();
      this.getPrivatePosts = this.getPrivatePosts.bind(this);
      this.state = {
        postData: []
      };
    }
    getPrivatePosts() {
      axios.get(`http://localhost:3000/Posts/private`).then(res => {
        const postData = res.data;
        this.setState({ postData });
      });
    }

    render() {
      return (
        <div>
          <button className="button" onClick={this.getPrivatePosts}>
            Show Private Posts
          </button>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>UserID</th>
                <th>Title</th>
                <th>Content</th>
                <th>isPrivate</th>
                <th>LastEdited</th>
              </tr>
            </thead>
            <tbody>
              {this.state.postData.map(post => (
                <tr key={post.ID}>
                  <td>{post.ID}</td>
                  <td>{post.UserID}</td>
                  <td> {post.Title}</td>
                  <td>{post.Content}</td>
                  <td>{post.isPrivate}</td>
                  <td>{
                    new Intl.DateTimeFormat('en-GB', { 
                    year: 'numeric', 
                    month: 'long', 
                    day: '2-digit' 
                    }).format(customer.firstSale)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  }

  export default Posts;
