import React, { Component } from 'react';
import { FormGroup , Label, Input, Button } from 'reactstrap';
import axios from 'axios';
class CommentField extends Component {
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            ID: '',
            UserID: '',
            PostID: '',
            Message: ''
        }; 
    }
    

    updateState = e => {
        const currUserId = sessionStorage.getItem("userID");
        const currPostId = e.target.id;

        this.setState({UserID: currUserId, PostID: currPostId});
    }

    handleSubmit(PostID) {
        

        // const { ID, UserID, Message  } = this.state;

        console.log({ PostID });
        // axios
        //     .post('http://localhost:3000/comments', { ID, PostID, UserID, Message })
        //     .then(res => {
        //     console.log(res);
        //     console.log(res.data);
        //     });
    };
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit(this.PostID)}>
                <FormGroup>
                    <Label for="exampleText">Text Area</Label>
                    <Input type="textarea" name="Message" id="exampleText" />
                    <Button type="submit">Comment</Button>
                </FormGroup>
                </form>
            </div>
        );
    }
}

export default CommentField;
