import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'react-bootstrap';
import axios from 'axios';

class PublicPosts extends Component {
constructor() {
    super();
    this.getPosts = this.getPosts.bind(this);
    this.state = {
      posts: []
    };
  }
  getPosts() {
    axios.get(`http://localhost:3000/posts/public`).then(res => {
      const posts = res.data;
      this.setState({ posts });
    });
  }
    render() {
        return (
        <div>
          <button className="button" onClick={this.getPosts}>
            Show posts
          </button>

          <ListGroup>
            <ListGroupItem header={Post.Title}>{Post.Content}</ListGroupItem>
          </ListGroup>



              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Content</th>
                <th>When posted</th>
                <th>Last Edited</th>
              </tr>

              {this.state.posts.map(Post => (
                <tr key={Post.ID}>
                  <td>{Post.ID}</td>
                  <td>{Post.Title}</td>
                  <td>{Post.Content}</td>
                  <td>{Post.WhenPosted}</td>
                  <td>{Post.LastEdited}</td>
                </tr>
              ))}

        </div>
        );
    }
}

export default PublicPosts;
