import React, { Component } from 'react';
import axios from 'axios';
import { DropdownButton, MenuItem } from 'react-bootstrap';
import Posts from './Posts';


class test extends Component {
    constructor() {
        super();
        this.getPosts = this.getPosts.bind(this);
        this.state = {
          posts: []
    
        };
      }
      getPosts() {
        axios.get(`http://localhost:3000/posts/public`).then(res => {
          const posts = res.data;
          this.setState({ posts });
        });
      }
    render() {
        return (

            <div>
            <button className="button" onClick={this.getPosts}>
                reload for posts
            </button>
            <div className="for-posts">
              {this.state.posts.map(Post =>(
            <div className="post-div">
              <h4>{Post.Title}</h4>
              <p>{Post.Content}</p>
            <DropdownButton>
              <MenuItem eventKey="1" text="a">Update</MenuItem>
              <MenuItem evetKey="2" text="b">Share</MenuItem>
              <MenuItem>Delete</MenuItem>
            </DropdownButton>
            </div>
            ))}
            </div>
            </div>
        );
    }
}

export default test;
