import React, { Component } from 'react';
import axios from 'axios';
import Users from './Users';


class ShareEntry extends Component {
    constructor() {
        super();
        this.onChange = this.onChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            ID: '',
            PostID: '',
            Owner: '',
            Viewer: ''
        };

        super();
            this.getUsers = this.getUsers.bind(this);
            this.state = {
              Users: []
            }; 
        }

        getUsers() {
            const username = document.getElementById("username").value;
            axios.get(`http://localhost:3000/Users/` + username).then(res => {
              const Users = res.data;
              this.setState({ Users });
              
              console.log(Users);
            });
          } 

        onChange = e => {
            const state = this.state;
            state[e.target.name] = e.target.value;
            this.setState(state);
        };
    
    handleSubmit = event => {
        event.preventDefault();

        const { PostID, Owner, Viewer } = this.state;
        window.location.reload();

        axios
            .post('http://localhost:3000/Shared/', { PostID, Owner, Viewer })
            .then(res => {
            console.log(res);
            console.log(res.data);
            });
        };


    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>FOR TEST PURPOSES POST ID:
                    <input type="number" name="PostID" placeholder="PostID" onChange={this.onChange}></input>
                    </label>
                    <br/>
                    <label>Share my(<input name="Owner" value="olek" onChange={this.onChange}></input>) entry with:
                    <input onKeyUp={this.getUsers} type="text" name="Viewer" placeholder="type in username" id="username"></input>
                    </label> 
             
                    <button type="submit">Share</button>
                </form>
                <table id="user_test">
                <thead>
              <tr>
                <th>ID</th>
                <th>username</th>
                <th>password</th>
              </tr>
            </thead>
                <tbody>
                {this.state.Users.map(user => (
                <tr key={user.ID}>
                  <td>{user.ID}</td>
                  <td>{user.username}</td>
                  <td> {user.password}</td>
                </tr>
              ))}   
                </tbody>
 
                </table>
            </div>

           

            
        );
    }
}

export default ShareEntry;
