import React, { Component } from 'react';
  import axios from 'axios';
  import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

  class Comments extends Component {
    constructor() {
      super();
      this.getComments = this.getComments.bind(this);
      this.toggle = this.toggle.bind(this);
      this.onChange = this.onChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.state = {
        CommentData: [],
        modal: false,
        ID: '',
        PostID: '',
        UserID: '',
        Message: '',
        LastEdit: '',
      };
    }
    getComments() {
      const ide = document.getElementById("postid").value
      axios.get(`http://localhost:3000/comments/` + ide).then(res => {
        const CommentData = res.data;
        this.setState({ CommentData });
      });
    }

    onChange = e => {
        const state = this.state;
        state[e.target.name] = e.target.value;
        this.setState(state);
    };

    handleSubmit = event => {
        event.preventDefault();

        const { ID, PostID, UserID, Message,LastEdit  } = this.state;

        axios
            PostID = document.getElementById("postid").value
            .post('http://localhost:3000/comments', { ID, PostID, UserID, Message, LastEdit })
            .then(res => {
            console.log(res);
            console.log(res.data);
            });
        };

    toggle() {
        this.setState({
          modal: !this.state.modal
        });
      }

    render() {
      const externalCloseBtn = <button className="close" style={{ position: 'absolute', top: '15px', right: '15px' }} onClick={this.toggle}>&times;</button>;

      return (
        <div>
          <input id="postid"></input>
          <button className="button" onClick={this.getComments}>
            Show post's comments
          </button>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>PostID</th>
                <th>UserID</th>
                <th>Message</th>
                <th>LastEdited</th>
              </tr>
            </thead>
            <tbody>
              {this.state.CommentData.map(comments => (
                <tr key={comments.ID}>
                  <td>{comments.ID}</td>
                  <td>{comments.PostID}</td>
                  <td>{comments.UserID}</td>
                  <td>{comments.Message}</td>
                  <td>
                  { comments.LastEdit.toString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div>
            <button onClick={this.toggle}>Add comment</button>
            <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} external={externalCloseBtn} >
            <ModalHeader></ModalHeader>
            <ModalBody>
                <div className="content">
                    <h2>Add comment</h2>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                        Post ID:
                        <input type="text" name="PostID" onChange={this.onChange} />
                        </label>
                        <label>
                        Your ID:
                        <input type="text" name="UserID" onChange={this.onChange} />
                        </label>
                        <label>
                        message:
                        <input type="text" name="Message" onChange={this.onChange} />
                        </label>
                        <label>
                        LastEdited:
                        <input type="text" name="LastEdit" onChange={this.onChange} />
                        </label>
                        <br/>
                        <button className="button" type="submit">
                        Add
                        </button>
                    </form>
                </div>
            </ModalBody>
            <ModalFooter>
                <Button color="secondary" onClick={this.toggle}>Close</Button>
            </ModalFooter>
            </Modal>

          </div>

        </div>
      );
    }
  }

  export default Comments;
