    render() {
        return (
            <div className="content">
                <h2>Delete Book</h2>
                <form onSubmit={this.handleSubmit}>
                    <label>
                    book_id:
                    <input type="number" name="book_id" onChange={this.onChange} />
                    </label>
                    <button className="button" type="submit">
                    Delete
                    </button>
                </form>
            </div>
        );
    }
}

export default DeleteBook;
