import React, { Component, Fragment } from 'react';
import CategoryDropdown from './CategoryDropdown';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSignInAlt } from '@fortawesome/free-solid-svg-icons';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem } from 'reactstrap';

class Navigator extends Component {
    constructor(props){
        super(props);
        this.toggleDD = this.toggleDD.bind(this);
        this.state = {
          isDDopen: false
        };
    }



    toggleDD() {
      this.setState({
        isDDopen: !this.state.isDDopen
      });
    }

    render() {
        return (
            <div>
            <Navbar expand="md" className="navi-bar">
              <NavLink to="/" style={{ textDecoration: 'none', color: 'black' }}>
                  <h3><i><b>Di</b>Log</i></h3>
              </NavLink>
              <CategoryDropdown/>

              <NavLink style={{ textDecoration: 'none', color: 'black' }} to="/Users/" />
              {sessionStorage.length !== 0
              ?
              <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret>
                Posts
              </DropdownToggle>
              <DropdownMenu right>
                <DropdownItem>
                  <NavLink className="dropdown-item" to="/CreatePost">
                    Create Post
                  </NavLink>
                </DropdownItem>
                <DropdownItem>
                  <NavLink className="dropdown-item" to="/posts/private">
                    View Private Posts
                  </NavLink>
                </DropdownItem>
                <DropdownItem>
                  <NavLink className="dropdown-item" to="/shared/mine">
                    View my shared posts
                  </NavLink>
                </DropdownItem>
                <DropdownItem>
                  <NavLink className="dropdown-item" to="/EditPost">
                    edit post
                  </NavLink>
                </DropdownItem>
                <DropdownItem>
                  <NavLink className="dropdown-item" to="/ShareEntry">
                    Share with
                  </NavLink>
                </DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
              :
              <div>
                  
                  <NavLink to="/login" style={{ textDecoration: 'none', color: 'black' }}>
                  <FontAwesomeIcon icon={faSignInAlt}/> Sign-in
                  </NavLink>
              </div>
              }
            </Navbar>
        </div>
        );
    }
}


export default Navigator;
