import React, { Component } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import OptionModal from './Components/OptionModal';
import { Badge } from 'reactstrap';
import { Route, Link } from 'react-router-dom';
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";


class test extends Component {

    constructor() {
        super();
        this.getPosts = this.getPosts.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
          posts: [],
          ID: '',
          toRemove: [],
        };
      }

      getPosts() {
        axios.get(`http://localhost:3000/posts/public`).then(res => {
          const posts = res.data;
          this.setState({ posts });
        });
      }

      handleSubmit = event => {
        event.preventDefault();
        const id = this.state.ID;
        const { ID } = this.state;

        axios.delete('http://localhost:3000/posts/' + id, {ID})
        .then(res => {
          console.log(res);
          console.log(res.data);
          });
      };

      undo = ID => {
        this.setState({
          toRemove: this.state.toRemove.filter(v => v !== ID)
        });
      }
      cleanPosts = () => this.setState({
        posts: this.state.posts.filter( v => !this.state.toRemove.includes(v.ID)),
        toRemove: []
      });
      removeDiv = e => {
        const ID = e.target.dataset.divId;
        this.setState({
          toRemove: [...this.state.toRemove, ID]
        });
      };

    render() {
      const { posts, toRemove } = this.state;
        return posts.filter(v => !toRemove.includes(v.ID)).map(v => (
